    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        try {
            String ID=TNO.getText();
            
            java.sql.Connection c=DatabaseConnection.getConnection();
            Statement s=c.createStatement();
            String q="SELECT `ID`,`NAME`,`AGE`,`GENDER`,`DATE`,`PHONE`,`LOCATION` FROM `user_register` WHERE ID='"+ID+"'";
            
            ResultSet rs=s.executeQuery(q);
            
            if (rs.next()){
             String u=rs.getString("name");
             tname.setText(u);
             String z=rs.getString("age");
             tage.setText(z);
             String g=rs.getString("gender");
             tgender.setText(g);
             String x=rs.getString("date");
             tdate.setText(x);
             String p=rs.getString("phone");
             tphone.setText(p);
             String y=rs.getString("location");
             tlocation.setText(y);
             
                
                
                
                
                
                
                
                
                
                
                
                
                
                
                
                
                
                
                
    
               
            }
            
            
            
               
            // TODO add your handling code here:
        } catch (SQLException ex) {
            Logger.getLogger(Adminframe.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

