    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        try {
            String name=namefield.getText();
            String age=agefield.getText();
            String date=datefield.getText();
            String phone=phonefield.getText();
            String location=locationcombo.getSelectedItem().toString();
            
            
            Radiomale.setActionCommand("Male");
            Radiofemale.setActionCommand("Female");
            Radioothers.setActionCommand("Others");
            
            String gender=buttonGroup1.getSelection().getActionCommand();
            
            
            
            System.out.println(name);
            System.out.println(age);
            System.out.println(date);
            System.out.println(phone);
            
            java.sql.Connection c=DatabaseConnection.getConnection();
            Statement st=c.createStatement();
               
            String query="INSERT INTO `user_register`( `NAME`, `AGE`, `GENDER`, `DATE`, `PHONE`, `LOCATION`) VALUES ('"+name+"','"+age+"','"+gender+"','"+date+"','"+phone+"','"+location+"')";
            st.executeUpdate(query);
            
            
            JOptionPane.showMessageDialog(rootPane,"Ready For Vaccine");
           
            
            
            
        } catch (SQLException ex) {
            Logger.getLogger(Vaccineframe.class.getName()).log(Level.SEVERE, null, ex);
        }
     
        
    }//GEN-LAST:event_jButton1ActionPerformed

