#include<iostream>
#include<cstdlib>
using namespace std;
int top;
template <class T>
class stack
{
    T stk[3];
    T x;
    public:
    stack()
{
top = -1;
}
T push()
{
    if(top>=2)
    {
        cout<<"Stack ovberflow";
    }
    else{
        cout<<"Enter the element";
        cin>>x;
        stk[++top] = x;
        cout<<"Inserted element \n"<<x;
    }
}
T pop()
{
    if(top<0)
    {
        cout<<"Stack underflow";
    }else
    {
        cout<<"delete element"<<stk[top--];
    }
}
T display()
{
    if(top<0)
    {
        cout<<"Stack is empty";
    }else
    {
        for (int i=top; i>=0;i++)
            cout<<stk[i]<<"";
    }
}
};
int main()
{
    int ch, type;
    stack <float>s2;
    stack <char>s3;
    stack<int>s1;
    while(1)
    {
        cout<<"\n 1.push, 2.pop, 3.display, 4.exit  enter your choice";
        cin>>ch;
        cout<<"\n int \n float \n char"<<endl<<"enter the type";
        cin>>type;
        switch (ch)
        {
        case 1: if(type==1)
        {
            s1.push();
        }else if (type==2)
        {
            s2.push();
        }
        else
        {
            s3.push();
        }
            break;

        case 2: if(type==1)
        {
            s1.pop();
        }
        else if(type==2)
        {
            s2.pop();
        }
        else{
            s3.pop();
        }
        break;

        case 3: if(type==1)
        {
            s1.display();
        }else if(type==2)
        {
            s2.display();

        }
        else{
            s3.display();
        }
        break;
        
        }
    }
    return 0;
}