section .data
%macro  display  2
mov  eax,4
mov  ebx,1
mov  ecx,%1
mov   edx,%2
int 80h
%endmacro
%macro read 2
mov  eax,3
mov  ebx,0
mov  ecx,%1
mov  edx,%2
int 80h
%endmacro
msg1  db "enter divident"
len1  equ $ -msg1
msg2 db "enter divisor" 
len2  equ $ -msg2
msg3  db "quotient"
len3  equ $ -msg3

section   .bss
num1  resb 2
num2  resb 2
 result  resb 2
 
 section  .text


global  _start
_start:
display  msg1,len1
read  num1,2
display  msg2,len2
read  num2,2
mov  al,[num1]
sub  al,'0'
mov  bl,[num2]
sub  bl ,'0'
div  bl
add  al,'0'
mov  [result],al

display  msg3,len3
display  result,2

mov  eax,1
mov  ebx,0
int 80h

