section  .data
%macro display  2 
MOV  eax,4
MOV  ebx,1
MOV  ecx,%1
MOV  edx,%2
int  80h
%endmacro
m1 db 'enter the multiplicant:'
l1 equ $-m1
m2 db 'enter the mutliplier :'
l2 equ $-m2
m3 db 'result :'
l3 equ $-m3
section  .bss 


n1  resb 2
n2  resb 2
product resb 2
section  .text


global  _start
_start:

display m1,l1
MOV  eax,3
MOV  ebx,0
MOV  ecx,n1
MOV  edx,2
int  80h

display m2,l2
MOV   eax,3
MOV  ebx,0
MOV  ecx,n2
MOV  edx,2
int  80h

mov  ah,0
MOV  al,[n1]
SUB  al,'0'
MOV  dl,[n2]
SUB  dl,'0'
mul dl

ADD  al,'0'
MOV [product],al
display m3,l3

mov   eax,4
MOV   ecx,product
MOV   edx,2
MOV   ebx,1
int  80h

MOV  eax,1
MOV  ebx,0
int  80h
